// 출처 : https://github.com/gitdagray/next-js-course/blob/main/next12/lib/posts.ts

/**
* GitHub 저장소에서 MDX 형식의 블로그 포스트를 가져와 파싱하는 함수
* 
* @param fileName - 가져올 MDX 파일의 이름
* @returns 파싱된 블로그 포스트 객체 또는 undefined
*/
export async function getPostByName(fileName: string): Promise<BlogPost | undefined> {
   // GitHub Raw Content URL로 MDX 파일 요청
   const res = await fetch(`https://raw.githubusercontent.com/gitdagray/test-blogposts/main/${fileName}`, {
       headers: {
           Accept: 'application/vnd.github+json', // GitHub API 응답 형식 지정
           Authorization: `Bearer ${process.env.GITHUB_TOKEN}`, // GitHub 인증 토큰
           'X-GitHub-Api-Version': '2022-11-28', // GitHub API 버전 명시
       }
   })

   // 요청 실패시 undefined 반환
   if (!res.ok) return undefined

   // 응답 텍스트(MDX 내용) 추출
   const rawMDX = await res.text()

   // 파일이 없는 경우 undefined 반환
   if (rawMDX === '404: Not Found') return undefined

   // MDX 컨텐츠 파싱
   const { frontmatter, content } = await compileMDX<{ title: string, date: string, tags: string[] }>({
       source: rawMDX,
       components: {
           Video, // MDX 내 사용될 커스텀 비디오 컴포넌트
           CustomImage, // MDX 내 사용될 커스텀 이미지 컴포넌트
       },
       options: {
           parseFrontmatter: true, // frontmatter 파싱 활성화
           mdxOptions: {
               rehypePlugins: [
                   rehypeHighlight, // 코드 하이라이팅
                   rehypeSlug, // 헤딩에 id 추가
                   [rehypeAutolinkHeadings, { // 헤딩에 자동 링크 추가
                       behavior: 'wrap'
                   }],
               ],
           },
       }
   })

   // 파일명에서 확장자 제거하여 포스트 ID 생성
   const id = fileName.replace(/\.mdx$/, '')

   // 블로그 포스트 객체 생성
   const blogPostObj: BlogPost = { 
       meta: { 
           id, 
           title: frontmatter.title, 
           date: frontmatter.date, 
           tags: frontmatter.tags 
       }, 
       content 
   }

   return blogPostObj
}

