// TilPage.tsx
// 클라이언트 컴포넌트에서 useEffect로 데이터 페칭
'use client';  // 클라이언트 컴포넌트 선언

useEffect(() => {
  fetch(`${baseUrl}/api/til`)
    .then(res => res.json())
    .then(data => {
      const entriesWithDates = data.entries.map((entry: any) => ({
        ...entry,
        date: new Date(entry.date),
      }));
      setTilEntries(entriesWithDates);
    });
}, []);

